import { configureStore, applyMiddleware } from "@reduxjs/toolkit";
import uiReducer from "./ui-slice";
import cartReducer from "./cart-slice";
import { composeWithDevTools } from "redux-devtools-extension"; // Import for DevTools
import thunk from 'redux-thunk'; // If you're using thunk or other middleware

const middleware = [thunk]; // Array to hold your middleware

const store = configureStore({
  reducer: { ui: uiReducer, cart: cartReducer },
  middleware: (getDefaultMiddleware) => getDefaultMiddleware().concat(middleware), // Add Middleware
  devTools: process.env.NODE_ENV !== 'production', // Enable DevTools only in development
});


export default store;
